mapper.update:
	update dicts
   	set editable = #{editable},
       item = #{item},
       type = #{type},
       value = #{value}
	where id = #{id}

mapper.select:
select *
from dicts d
where d.id = #{id}

select * from
(select rownum rn,d.*
from dicts d
)

where rn >= #{fromIndex} and rn < #{endIndex}
