mapper.update:
update users
   set enabled = #{enabled},
       name = #{name},
       password = #{password},
       role_id = #{role.id}
 where id = #{id}

mapper.select:
select * from
(select rownum rn,id, enabled, name, 
password, role_id as "role.id", salt
from users u
)

where rn >= #{fromIndex} and rn <#{endIndex}
